import React, {Component} from "react";
import '../../statics/style/audioqq.css'

class Audio extends Component {
    constructor(props) {
        super(props);
        this.state = {
            // url: "https://music.163.com/song/media/outer/url?id=1349292048.mp3", //歌曲地址
            url: "http://m801.music.126.net/20210324180243/36f09a079ebb8bb73f53d2f59350f205/jdymusic/obj/wo3DlMOGwrbDjj7DisKw/7907957215/bd71/3501/9e35/299b1b928be11f1555229991e484985e.mp3",

            current: "00:00", //当前时间
            allTime: "00:00", //当前歌曲时长
        };
        this.outer = React.createRef(); //自定义进度条外框
        this.inner = React.createRef(); //自定义进度条内核
        this.audio = React.createRef();
    }

    componentDidMount() {
        // console.log(this.audio);
        this.canplay();
        this.timeUpdate();
        this.handleAudio();
    }

    //在音频可以播放时就显示总时长
    canplay = () => {
        this.audio.current.oncanplay = () => {
            var allTime =
                (Math.floor(this.audio.current.duration / 60) + "").padStart(2, "0") +
                ":" +
                (Math.floor(this.audio.current.duration % 60) + "").padStart(2, "0");
            this.setState({
                allTime
            });
        };
    }

    audioWaiting = () => {
        this.audio.current.onwaiting = () => {
            alert("加载中");
        };
    }

    //在音频播放时当前时间也跟着变化
    timeUpdate = () => {
        this.audio.current.ontimeupdate = () => {
            // console.log(this.audio.current.currentTime)
            // console.log(this.audio.current.duration)
            var currentTime =
                (Math.floor(this.audio.current.currentTime / 60) + "").padStart(2, "0")
                + ":" +
                (Math.floor(this.audio.current.currentTime % 60) + "").padStart(2, "0");
            this.setState({
                current: currentTime
            });
            this.inner.current.style.width =
                (this.audio.current.currentTime / this.audio.current.duration) * 100 +
                "%";
        };
    }

    //播放开始
    audioPlay = () => {
        this.audio.current.play();
    }

    //播放暂停
    audioStop = () => {
        this.audio.current.pause();
    }


    //设置播放进度
    audioX = (e) => {
        console.log('鼠标到可视区左侧的距离', e.targetTouches[0].clientX);
        var outerWidth = this.outer.current.offsetWidth; //父元素宽
        var innerWidth = e.targetTouches[0].clientX //鼠标到左侧的距离
        if (innerWidth > outerWidth || innerWidth < 0) {
            return;
        }
        this.inner.current.style.width = innerWidth + "px";
        this.audio.current.currentTime =
            (innerWidth / outerWidth) * this.audio.current.duration;
    }

    handleAudio = () => {
        let audio = this.audio.current;
        // timeupdate,播放位置改变时触发
        audio.addEventListener("timeupdate", () => {
            console.log("播放位置改变");
        });
        // canplay,表示音频可以播放了，准备就绪
        audio.addEventListener("canplay", () => {
            console.log("可以播放了");
        });
        // progress，音频在加载或缓冲
        audio.addEventListener("progress", () => {
            console.log("音频在加载或缓冲");
        });
        // waiting,需要缓冲下一帧而停止播放时触发
        audio.addEventListener("waiting", () => {
            console.log("需要缓冲下一帧");
        });
        // playing,音频或视频已开始播放时触发
        audio.addEventListener("playing", () => {
            console.log("开始播放");
        });
        // pause,音频或视频文件暂停时触发
        audio.addEventListener("pause", () => {
            console.log("已经暂停");
        });
    }

    render() {
        const {url, current, allTime} = this.state;
        return (
            <div>
                <h1>当前时间：{current}</h1>
                <h1>总时间：{allTime}</h1>
                <div
                    className="outer"
                    ref={this.outer}
                    onTouchMove={e => this.audioX(e)}
                    onTouchStart={e => this.audioX(e)}
                >
                    <div className="inner" ref={this.inner}>
                        <div className="innerBtn"></div>
                    </div>
                </div>
                <br/>
                <br/>
                <br/>
                <audio ref={this.audio} src={url}>
                    你的浏览器不支持音频播放哦！(;´༎ຶД༎ຶ`)
                </audio>
                <button onClick={() => this.audioPlay()}>播放</button>
                <button onClick={() => this.audioStop()}>暂停</button>
            </div>
        );
    }
}

export default Audio;
