const defaultState = {
    bannerList: [],
    imageIndex: 0,
    newSongList: [],
    newSongList2: [],
    username: '',
    password: '',
    isLogin: false,
    code: '',
    userInfo: {}
    // newsongClickIndex: ''
    // inputValue: '',
    // list: []
}

export default (state=defaultState, action)=>{
    if(action.type === 'get_username_value'){
        const newState = JSON.parse(JSON.stringify(state))
        newState.username = action.value
        return newState
    }
    if(action.type === 'get_password_value'){
        const newState = JSON.parse(JSON.stringify(state))
        newState.password = action.value
        return newState
    }
    if(action.type === 'get_userInfo'){
        const newState = JSON.parse(JSON.stringify(state))
        newState.userInfo = action.value
        return newState
    }
    if(action.type === 'get_banner_list'){
        const newState = JSON.parse(JSON.stringify(state))
        newState.bannerList = action.value
        return newState
    }
    if(action.type === 'get_newsong_value'){
        const newState = JSON.parse(JSON.stringify(state))
        newState.newSongList = action.value
        return newState
    }
    if(action.type === 'change_isLogin_login'){
        const newState = JSON.parse(JSON.stringify(state))
        newState.isLogin = action.value
        return newState
    }
    if(action.type === 'change_isLogin_logout'){
        const newState = JSON.parse(JSON.stringify(state))
        newState.isLogin = action.value
        console.log('lbtlbt', newState.isLogin)
        return newState
    }
    // if(action.type === 'save_click_index'){
    //     const newState = JSON.parse(JSON.stringify(state))
    //     newState.newsongClickIndex = action.index
    //     return newState
    // }
    return state;
}
