import React, {Component} from "react";
import '../../statics/style/audio.css'

class Audio extends Component {
    constructor(props) {
        super(props);
        this.state = {
            url: this.props.audioUrl,
            currentTime: "00:00",
            totalTime: "00:00",
            isPlay: false
        };
        this.outside = React.createRef(); //自定义进度条外框
        this.inner = React.createRef(); //自定义进度条内核
        this.audio = React.createRef();
    }

    componentDidMount() {
        this.canplay();
        this.timeUpdate();
        this.handleAudio();
    }

    //在音频可以播放时就显示总时长
    canplay = () => {
        this.audio.current.oncanplay = () => {
            var totalTime =
                (Math.floor(this.audio.current.duration / 60) + "").padStart(2, "0") +
                ":" +
                (Math.floor(this.audio.current.duration % 60) + "").padStart(2, "0");
            this.setState({
                totalTime
            });
        };
    }


    //在音频播放时当前时间也跟着变化
    timeUpdate = () => {
        this.audio.current.ontimeupdate = () => {
            var currentTime =
                (Math.floor(this.audio.current.currentTime / 60) + "").padStart(2, "0")
                + ":" +
                (Math.floor(this.audio.current.currentTime % 60) + "").padStart(2, "0");
            this.setState({
                currentTime: currentTime
            });
            this.inner.current.style.width =
                (this.audio.current.currentTime / this.audio.current.duration) * 100 +
                "%";
        };
    }

    //播放开始
    audioPlay = () => {
        this.setState({
            isPlay: true
        })
        this.audio.current.play();
    }

    //播放暂停
    audioStop = () => {
        this.setState({
            isPlay: false
        })
        this.audio.current.pause();
    }


    //设置播放进度
    audioX = (e) => {
        console.log('鼠标到可视区左侧的距离', e.targetTouches[0].clientX);
        let outsideWidth = this.outside.current.offsetWidth;
        let insideWidth = e.targetTouches[0].clientX
        if (insideWidth > outsideWidth || insideWidth < 0) {
            return;
        }
        this.inner.current.style.width = insideWidth + "px";
        this.audio.current.currentTime =
            (insideWidth / outsideWidth) * this.audio.current.duration;
    }

    handleAudio = () => {
        let audio = this.audio.current;
        // timeupdate,播放位置改变时触发
        audio.addEventListener("timeupdate", () => {
            console.log("播放位置改变");
        });
        // canplay,表示音频可以播放了，准备就绪
        audio.addEventListener("canplay", () => {
            console.log("可以播放了");
        });
        // progress，音频在加载或缓冲
        audio.addEventListener("progress", () => {
            console.log("音频在加载或缓冲");
        });
        // waiting,需要缓冲下一帧而停止播放时触发
        audio.addEventListener("waiting", () => {
            console.log("需要缓冲下一帧");
        });
        // playing,音频或视频已开始播放时触发
        audio.addEventListener("playing", () => {
            console.log("开始播放");
        });
        // pause,音频或视频文件暂停时触发
        audio.addEventListener("pause", () => {
            console.log("已经暂停");
        });
    }

    render() {
        return (
            <div className="audio">
                <span>{this.state.currentTime}</span>
                <div
                    className="audio-outside"
                    ref={this.outside}
                    onTouchMove={e => this.audioX(e)}
                    onTouchStart={e => this.audioX(e)}
                >
                    <div
                        className="audio-inside"
                        ref={this.inner}
                    >
                        <div className="audio-inside-btn"></div>
                    </div>
                </div>
                <span>{this.state.totalTime}</span>
                    <audio
                        ref={this.audio}
                        src={this.state.url}
                    ></audio>
                <div className="audio-btn">
                    {!this.state.isPlay
                        ? <i
                            className="iconfont icon-bofang"
                            onClick={() => this.audioPlay()}
                        ></i>
                        : <i
                            className="iconfont icon-zanting"
                            onClick={() => this.audioStop()}
                        ></i>
                    }
                </div>
            </div>
        )
    }
}

export default Audio;
