import React, {Component, Fragment} from 'react';
import '../../statics/style/newsong.css';
import store from "../../store";

class NewSongItem extends Component {
    constructor(props) {
        super(props);
        this.state = store.getState()
    }

    onNewsongPlayClick = () => {
        const action1 = {
            type: 'change_isPlaying_true',
        }
        store.dispatch(action1)
        const action2 = {
            type: 'change_showBar_true',
        }
        store.dispatch(action2)
        if(!this.props.onNewsongPlayClick){return}
        this.props.onNewsongPlayClick(this.props.newsongId)
    }

    onNewsongPauseClick = () => {
        const action = {
            type: 'change_isPlaying_false'
        }
        store.dispatch(action)
    }

    render() {
        return (
            <div className="newsongItem">
                <div className="newsongItem-outside">
                    <div className="newsongItem-inside">
                        <div>
                            {this.props.value.id ? this.props.index + 1 : ''}
                        </div>
                        <div className="newsongItem-avatar">
                            <img src={this.props.value.imageUrl}/>
                        </div>
                        <div className="newsongItem-info">
                            <span>{this.props.value.artistName}</span>
                            <p>{this.props.value.name}</p>
                        </div>
                        <div className="newsongItem-btn">
                            {!this.props.mp3Url
                                ? <i
                                className="iconfont icon-zanting"
                                onClick={this.onNewsongPauseClick}
                                ></i>
                                : <i
                                className="iconfont icon-bofang"
                                onClick={this.onNewsongPlayClick}
                                ></i>
                            }
                        </div>
                        {/*<button*/}
                        {/*    onClick={this.onNewsongPlayClick(value.id)}*/}
                        {/*>*/}
                        {/*    123*/}
                        {/*</button>*/}
                    </div>
                </div>
            </div>
        )
    }
}

export default NewSongItem;
