                            <p>{this.props.value.name}</p>
                        </div>
                        {/*<div className="newsongItem-mp3">*/}
                            {/*<audio*/}
                            {/*    src={this.props.mp3Url}*/}
                            {/*    controls*/}
                            {/*></audio>*/}
                        {/*</div>*/}
                        <div className="newsongItem-btn">
                            {this.props.mp3Url
                                ? <i
                                    className="iconfont icon-bofang"
                                    onClick={this.onMusicPlayClick}
                                ></i>
                                : <i className="iconfont icon-zanting"></i>
                            }
                        </div>
                        {/*<button*/}
                        {/*    onClick={this.onNewsongPlayClick(value.id)}*/}
                        {/*>*/}
                        {/*    123*/}
                        {/*</button>*/}
                    </div>
                </div>
            </div>
        )
    }
}

export default NewSongItem;
