import React, {Component} from "react";
import '../../statics/style/audio.css'

class Audio extends Component {
    constructor(props) {
        super(props);
        this.state = {
            url: this.props.audioUrl,
            currentTime: "00:00",
            totalTime: "00:00",
            isPlay: false
        };
        this.outside = React.createRef(); //自定义进度条外框
        this.inside = React.createRef(); //自定义进度条内核
        this.audio = React.createRef();
    }

    componentDidMount() {
        this.canplay();
        this.timeUpdate();
    }

    // formatTime = (time) => {
    //     return(

    //在音频可以播放时就显示总时长
    canplay = () => {
        this.audio.current.oncanplay = () => {
            const totalTime = (Math.floor(this.audio.current.duration / 60) + "").padStart(2, "0") +
                ":" +
                (Math.floor(this.audio.current.duration % 60) + "").padStart(2, "0");
            this.setState({
                totalTime
            });
        };
    }


    //在音频播放时当前时间也跟着变化
    timeUpdate = () => {
        this.audio.current.ontimeupdate = () => {
            const currentTime =
                (Math.floor(this.audio.current.currentTime / 60) + "").padStart(2, "0")
                + ":" +
                (Math.floor(this.audio.current.currentTime % 60) + "").padStart(2, "0");
            this.setState({
                currentTime
            });
            this.inside.current.style.width =
                (this.audio.current.currentTime / this.audio.current.duration) * 100 + "%";
        };
    }

    //播放开始
    audioPlay = () => {
        this.setState({
            isPlay: true
        })
        this.audio.current.play();
    }

    //播放暂停
    audioStop = () => {
        this.setState({
            isPlay: false
        })
        this.audio.current.pause();
    }


    //设置播放进度
    onProgressChange = (e) => {
        let outsideWidth = this.outside.current.offsetWidth;
        let insideWidth = e.targetTouches[0].clientX
        if (insideWidth > outsideWidth || insideWidth < 0) {
            return;
        }
        this.inside.current.style.width = insideWidth + "px";
        this.audio.current.currentTime = (insideWidth / outsideWidth) * this.audio.current.duration;
    }

    render() {
        return (
            <div className="audio">
                <div className="audio-img">
                    <img
                        className={this.state.isPlay ? 'animation-img' : ''}
                        src={this.props.currentSongItem.imageUrl}
                    />
                </div>
                <div className="audio-info">
                    <span>{this.props.currentSongItem.artistName}</span>
                    <p>{this.props.currentSongItem.name}</p>
                </div>
                <div className="audio-progress">
                    <span>{this.state.currentTime}</span>
                    <div
                        className="audio-outside"
                        ref={this.outside}
                        onTouchMove={(e) => this.onProgressChange(e)}
                        onTouchStart={(e) => this.onProgressChange(e)}
                    >
                        <div
                            className="audio-inside"
                            ref={this.inside}
                        >
                            <div className="audio-inside-btn"></div>
                        </div>
                    </div>
                    <span>{this.state.totalTime}</span>
                    <audio
                        ref={this.audio}
                        src={this.state.url}
                    ></audio>
                    <div className="audio-btn">
                        {!this.state.isPlay
                            ? <i
                                className="iconfont icon-bofang"
                                onClick={this.audioPlay}
                            ></i>
                            : <i
                                className="iconfont icon-zanting"
                                onClick={this.audioStop}
                            ></i>
                        }
                    </div>
                </div>
            </div>
        )
    }
}

export default Audio;
