import React, { Component } from 'react';
import '../../statics/style/banner.css'

class Banner extends Component{
    constructor(props) {
        super(props);
    }

    state = {
        bannerIndex: this.props.index
    }

    autoChangeImage = () => {
        if (this.state.bannerIndex === this.props.bannerList.length - 1) {
            this.setState(()=>{
                return {
                    bannerIndex : 0
                }
            })
        }
        else{
            this.setState(()=>{
                const newBannerIndex = this.state.bannerIndex
                return {
                    bannerIndex: newBannerIndex+1
                }
            })
        }
    }

    componentDidMount() {
        setInterval(()=>{
            this.autoChangeImage()
        }, 1000)
    }

    render(){
        return(
            <div className="banner-container">
                <div className="banner-wrap">
                    {this.props.bannerList.map((item, index)=>{
                        return (
                            <div
                                className="banner-image"
                            >
                                <img
                                    src={item}
                                    key={index}
                                    style={{'display' : index === this.props.imageIndex ? 'block' : 'none'}}/>
                            </div>
                        )
                    })
                    }
                </div>
                <ol>
                    {this.props.bannerList.map((index, liIndex)=>{
                        return (
                            <li
                                style={{'display' : index === this.props.a ? 'initial' : 'circle'}}
                            >.</li>
                        )
                    })}
                </ol>
            </div>
        )
    }
}

export default Banner;
