import React, { Component } from 'react';
import Header from '../../components/header';
import Banner from '../../components/banner';
import NewSong from '../../components/newsong';
import '../../statics/style/home.css';
import axios from 'axios';
import store from '../../store';
// import { getBanner } from '../../services/api'

class Home extends Component{
    constructor(props) {
        super(props);
        this.state = store.getState()
        store.subscribe(this.onHandleSetState)
    }

    componentDidMount() {
        this.getBanner()
    }

    getBanner = () => {
        axios.get('http://localhost:3000/banner?type=0')
            .then((value) => {
                const { ...banners } = value.data.banners
                const bannerImageUrl = []
                for(let i=0 ; i < value.data.banners.length; i++){
                    bannerImageUrl.push(banners[i].imageUrl)
                }
                console.log(bannerImageUrl[1])

                const action ={
                    type: 'get_banner_list',
                    value: value
                }
                store.dispatch(action)
            })
            .catch((error)=>{
                console.log('banner error', error)
            })
    }

    onHandleSetState = () => {
        this.setState(store.getState())
    }

    render(){
        return(
            <div className="home-div">
                <div>
                    <Header/>
                </div>
                <div>
                    <Banner
                        bannerList={this.state.bannerList}
                    />
                </div>
                <div>
                    <NewSong/>
                </div>
            </div>
        )
    }
}

export default Home;
