import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Redirect } from 'react-router';
import store from '../../store';
import '../../statics/style/header.css';


class Header extends Component {
    constructor(props) {
        super(props);
        this.state = store.getState()
    }

    onLogoutClick = () => {
        const action = {
            type: 'change_isLogin_logout',
            value: false
        }
        store.dispatch(action)
    }

    render() {
        return (
            <div className="nav-div">
                <ul className="nav-ul">
                    <li>
                        <Link key='logo' to="/">
                            <div className="nav-logo"></div>
                        </Link>
                    </li>
                    <li>
                        {this.state.isLogin
                            ? <Link key='recommend' to="/recommend">推荐</Link>
                            : <Redirect to="/login">推荐</Redirect>
                        }
                    </li>
                    <li>
                        {this.state.isLogin
                            ? <Link key='rank' to="/rank">排行榜</Link>
                            : <Redirect to="/login">排行榜</Redirect>
                        }
                    </li>
                    <li>
                        {this.state.isLogin
                            ? <Link key='songsheet' to="/songsheet">歌单</Link>
                            : <Redirect to="/login">歌单</Redirect>
                        }
                    </li>
                    <li>
                        {this.state.isLogin
                            ? <Link key='radio' to="/radio">主播电台</Link>
                            : <Redirect to="/login">主播电台</Redirect>
                        }
                    </li>
                    <li>
                        {this.state.isLogin
                            ? <Link key='singer' to="/singer">歌手</Link>
                            : <Redirect to="/login">歌手</Redirect>
                        }
                    </li>
                    <li>
                        {this.state.isLogin
                            ? <Link key='onsale' to="/onsale">新碟上架</Link>
                            : <Redirect to="/login">新碟上架</Redirect>
                        }
                    </li>
                    <li>
                        {
                            this.state.isLogin
                                ? <div onClick={this.onLogoutClick}>退出</div>
                                : <Link key='login' to="/login">登录</Link>
                        }
                    </li>
                </ul>
            </div>
        )
    }
}

export default Header;
