import React, { Component } from 'react';
import '../../statics/style/banner.css'

class Banner extends Component{
    constructor(props) {
        super(props);
    }


    render(){
        return(
            <div className="banner-container">
                <div className="banner-wrap">
                    {this.props.bannerList.map((item, index)=>{
                        return (
                            <div
                                className="banner-image"
                            >
                                <img
                                    src={item}
                                    key={index}
                                    style={{'display' : index === this.props.imageIndex ? 'block' : 'none'}}/>
                            </div>
                        )
                    })
                    }
                </div>
                <ol>
                    {this.props.bannerList.map((index, a)=>{
                        console.log('lbt', a)
                        return (
                            <li
                                style={{'display' : index === this.props.imageIndex ? 'initial' : 'circle'}}
                            >.</li>
                        )
                    })}
                </ol>
            </div>
        )
    }
}

export default Banner;
