import React, {Component, Fragment} from 'react';
import '../../statics/style/newsong.css';

class NewSongItem extends Component {
    constructor(props) {
        super(props);
    }

    onNewsongPlayClick = () => {
        if(!this.props.onNewsongPlayClick){return}
        this.props.onNewsongPlayClick(this.props.newsongId)
    }

    render() {
        return (
            <div className="newsongItem-outside">
                <div className="newsongItem-inside">
                    <div
                        onClick={this.onNewsongPlayClick}
                    >
                        {this.props.value.id ? this.props.index + 1 : ''}
                    </div>
                    <div className="newsongItem-avatar">
                        <img src={this.props.value.imageUrl}/>
                    </div>
                    <div className="newsongItem-info">
                        <p>{this.props.value.artistName}</p>
                        <div className="newsongItem-processbar"
                        >
                            {}
                        </div>
                    </div>
                    <div>
                        <p></p>
                    </div>
                    <div>
                        {
                            (this.props.mp3Url
                                ? <i className="iconfont icon-bofang"></i> : '')
                            // : <i className="iconfont icon-zanting"></i>
                        }
                    </div>
                    {/*<button*/}
                    {/*    onClick={this.onNewsongPlayClick(value.id)}*/}
                    {/*>*/}
                    {/*    123*/}
                    {/*</button>*/}
                </div>
            </div>
        )
    }
}

export default NewSongItem;
