import axios from "axios";
import store from "../store";
const storeValue = store.getState()

export const getBanner = () => {
    axios.get('http://localhost:3000/banner?type=0')
        .then((value) => {
            const { ...banners } = value.data.banners
            const bannerImageUrl = []
            for(let i=0 ; i < value.data.banners.length; i++){
                bannerImageUrl.push(banners[i].imageUrl)
            }
            const action ={
                type: 'get_banner_list',
                value: bannerImageUrl
            }
            store.dispatch(action)
        })
        .catch((error)=>{
            console.log('getBanner error', error)
        })
}

export const getNewsong = () => {
    const newSongItem = {
        id: '',
        name: '',
        imageUrl: '',
    }
    const newSongList = []
    axios.get('http://localhost:3000/personalized/newsong')
        .then((value)=> {
                for (let i = 0; i < value.data.result.length; i++) {
                    const {...newsongData} = value.data.result
                    newSongItem.id = newsongData[i].id;
                    newSongItem.name = newsongData[i].name;
                    newSongItem.imageUrl = newsongData[i].picUrl;
                    newSongList.push({...newSongItem})
                    for (let j = 0; j < newSongList.length; j++) {
                        axios.post(
                            'http://localhost:3000/song/url',
                            {id: newSongList[j].id}
                        ).then((value) => {
                            console.log(value.data.data[0]);
                        }).catch(() => {

                        })
                    }
                }
            const action = {
                type: 'get_newsong_value',
                value: newSongList
            }
            store.dispatch(action)
        })
        .catch((error)=>{
            console.log('getNewsong error', error)
        })
}

export const getSongUrl = () => {

}
