import React, { Component } from 'react';
import Header from '../../components/header';
import Banner from '../../components/banner';
import NewSongItem from '../../components/newsongitem';
import '../../statics/style/home.css';
import store from '../../store';
import {getBanner, getNewsong, getNewsongDetail } from '../../services/api'

class Home extends Component{
    constructor(props) {
        super(props);
        this.state = store.getState()
    }

    componentDidMount() {
        store.subscribe(this.onHandleSetState)
        getBanner()
        getNewsong()
        getNewsongDetail()
    }

    onHandleSetState = () => {
        this.setState(store.getState())
    }

    // onNewsongPlayClick = (index) => {
    //     console.log('parent', index)
    //     const action = {
    //         type: 'save_click_index',
    //         index
    //     }
    //     store.dispatch(action)
    // }

    onNewsongPlayClick = (index) => {
        console.log('lbtlbt onNewsongPlayClick', index)
    }

    render(){
        return(
            <div className="home-div">
                <div>
                    <Header/>
                </div>
                <div>
                    <Banner
                        bannerList={this.state.bannerList}
                    />
                </div>
                <div className="home-newsong">
                    {this.state.newSongList.map((value, index)=>{
                        return (
                            <NewSongItem
                                value={value}
                                index={index}
                                newsongId={value.id}
                                onNewsongPlayClick={this.onNewsongPlayClick}
                            />
                        )
                    })}
                </div>
            </div>
        )
    }
}

export default Home;
