import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Redirect } from 'react-router';
import store from '../../store';
import '../../statics/style/header.css';


class Header extends Component {
    constructor(props) {
        super(props);
        this.state = store.getState()
    }

    render() {
        return (
            <div className="nav-div">
                <ul className="nav-ul">
                    <li>
                        <Link to="/">
                            <div className="nav-logo"></div>
                        </Link>
                    </li>
                    <li>
                        {this.state.isLogin
                            ? <Link to="/recommend">推荐</Link>
                            : <Redirect to="/login">推荐</Redirect>
                        }
                    </li>
                    <li>
                        {this.state.isLogin
                            ? <Link to="/rank">排行榜</Link>
                            : <Redirect to="/login">排行榜</Redirect>
                        }
                    </li>
                    <li>
                        {this.state.isLogin
                            ? <Link to="/songsheet">歌单</Link>
                            : <Redirect to="/login">歌单</Redirect>
                        }
                    </li>
                    <li>
                        {this.state.isLogin
                            ? <Link to="/radio">主播电台</Link>
                            : <Redirect to="/login">主播电台</Redirect>
                        }
                    </li>
                    <li>
                        {this.state.isLogin
                            ? <Link to="/singer">歌手</Link>
                            : <Redirect to="/login">歌手</Redirect>
                        }
                    </li>
                    <li>
                        {this.state.isLogin
                            ? <Link to="/onsale">新碟上架</Link>
                            : <Redirect to="/login">新碟上架</Redirect>
                        }
                    </li>
                    <li>
                        {
                            this.state.isLogin
                                ? <Redirect to="/login">
                                    <span onClick='onLogoutClick'>退出</span>
                                    </Redirect>
                                : <Link to="/login">登录</Link>
                        }
                    </li>
                </ul>
            </div>
        )
    }
}

export default Header;
