import React, {Component, Fragment} from 'react';
import '../../statics/style/newsong.css';

class NewSong extends Component{
    constructor(props) {
        super(props);
    }

    onNewsongPlayClick = (event) => {
        event.stopPropagation();
        console.log('this.span',this.span)
        if(!this.props.onNewsongPlayClick){ return }
        this.props.onNewsongPlayClick()
    }

    render(){
        return(
            <Fragment>
                {this.props.newsongList.map((value, index)=>{
                    return(
                        <div className="newsongItem-outside">
                            <div className="newsongItem-inside" key={value} ref={()=>this.span}>
                                <span
                                    onClick={this.onNewsongPlayClick()}
                                >{value.id ? index + 1 : '' }</span>
                                <div className="newsongItem-avatar">
                                    <img src={value.imageUrl}/>
                                </div>
                                <div className="newsongItem-info">
                                    <p>{value.name}</p>
                                    <div className="newsongItem-processbar"
                                    >
                                        {}
                                    </div>
                                </div>
                                <div>
                                    <p></p>
                                </div>
                                {/*<button*/}
                                {/*    onClick={this.onNewsongPlayClick(value.id)}*/}
                                {/*>*/}
                                {/*    123*/}
                                {/*</button>*/}
                            </div>
                        </div>
                    )
                })}
            </Fragment>
        )
    }
}

export default NewSong;
