import React, { Component } from 'react';
import Header from '../../components/header';
import Banner from '../../components/banner';
import NewSong from '../../components/newsong';
import '../../statics/style/home.css';
import store from '../../store';
import {getBanner, getNewsong } from '../../services/api'

class Home extends Component{
    constructor(props) {
        super(props);
        this.state = store.getState()
    }

    componentDidMount() {
        store.subscribe(this.onHandleSetState)
        getBanner()
        getNewsong()
    }

    onHandleSetState = () => {
        this.setState(store.getState())
    }

    // onNewsongPlayClick = (index) => {
    //     console.log('parent', index)
    //     const action = {
    //         type: 'save_click_index',
    //         index
    //     }
    //     store.dispatch(action)
    // }

    render(){
        return(
            <div className="home-div">
                <div>
                    <Header/>
                </div>
                <div>
                    <Banner
                        bannerList={this.state.bannerList}
                    />
                </div>
                <div className="home-newsong">
                    <NewSong
                        newsongList={this.state.newSongList}
                        // onNewsongPlayClick={this.onNewsongPlayClick}
                    />
                </div>
            </div>
        )
    }
}

export default Home;
