import React, { Component } from 'react';
import store from '../../store'
import '../../statics/style/login.css'
import {getLogin} from "../../services/api";

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = store.getState()
    }

    componentDidMount() {
        store.subscribe(this.onHandleSetState)
    }

    onHandleSetState = () => {
        this.setState(store.getState())
    }

    onUsernameChange = (e) => {
        const action = {
            type: 'get_username_value',
            value: e.target.value
        }
        store.dispatch(action)
    }

    onPasswordChange = (e) => {
        const action = {
            type: 'get_password_value',
            value: e.target.value
        }
        store.dispatch(action)
    }

    onLoginBtn = () => {
        const phone = this.state.username
        const pwd = this.state.password
        getLogin(phone, pwd)
        if(this.state.isLogin == true){
            console.log("sghdgsiohgs")
            this.props.history.push('/home')
        }else{
            console.log('lbtlbt jump error')
        }
    }

    render() {
        return(
            <div className="login-outside">
                <div className="login-inside">
                    <div className="login-img"></div>
                    <div className="login-content">
                        <input
                            type="text"
                            placeholder="telephone"
                            value={this.state.username}
                            onChange={this.onUsernameChange}
                        />
                        <input
                            type="password"
                            placeholder="password"
                            value={this.state.password}
                            onChange={this.onPasswordChange}
                        />
                        <div className="login-btn">
                            <button
                                onClick={this.onLoginBtn}
                             >LOGIN</button>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default Login
