import React, {Component} from "react";
import '../../statics/style/playbar.css';
// import store from '../../store';

class PlayBar extends Component {
    constructor(props) {
        super(props);
        this.audio = React.createRef()
        this.state = store.getState()
    }

    formatDuration = (value) => {
        const time = value/1000
        const min = Math.floor(time / 60)
        min.toString().padStart(2, '0')
        const sec = Math.floor(time % 60)
        sec.toString().padStart(2, '0')
        return `${min}:${sec}`
    }

    componentDidMount() {
        this.audio.current.onended = () => {
            this.nextSong()
        }
    }

    nextSong = () => {

    }

    preSong = () => {

    }


    onPauseMusicClick = () => {
        this.setState({
            playStatus: false
        })
        console.log('ref', this.audio)
        this.audio.current.pause()
    }

    onPlayMusicClick = () => {
        this.setState({
            playStatus: true
        })
        this.audio.current.play()
    }

    render() {

        return (
            <div className="playbar">
                <div className="playbar-audio">
                    <audio
                        id="mp3btn"
                        ref={this.audio}
                        src={this.props.mp3Url}
                        // style={{visibility: "hidden"}}
                    >
                    </audio>
                </div>
                <div className="playbar-img">
                    <img src={this.props.currentSongItem.imageUrl}/>
                </div>
                <div className="playbar-info">
                    <span>{this.props.currentSongItem.artistName}</span>
                    <p>{this.props.currentSongItem.name}</p>
                </div>
                <div className="playbar-progress">
                    <label>00:00</label>
                    <progress max={this.formatDuration(this.props.currentSongItem.duration)}
                              value={'currentTime'}></progress>
                    <label>{this.formatDuration(this.props.currentSongItem.duration)}</label>
                </div>
                <div className="playbar-btn">
                    <i className="iconfont icon-shangyige"></i>
                    {
                        this.state.isPlaying
                            ? <i
                                className="iconfont icon-zanting"
                                onClick={this.onPauseMusicClick}
                            ></i>
                            : <i
                                className="iconfont icon-bofang"
                                onClick={this.onPlayMusicClick}
                            ></i>
                    }
                    <i className="iconfont icon-xiayige"></i>
                </div>
                <div className="playbar-lyrics">
                    <i className="iconfont icon-geci"></i>
                </div>
            </div>
        )
    }
}

export default PlayBar;
