import React, { Component } from 'react';
import Header from '../../components/header';
import Banner from '../../components/banner';
import NewSongItem from '../../components/newsongitem';
import Footer from '../../components/footer';
import '../../statics/style/home.css';
import store from '../../store';
import {getBanner, getSongUrlById, getTopSong } from '../../services/api'
import {BackTop} from "antd";

class Home extends Component{
    constructor(props) {
        super(props);
        this.state = store.getState()
    }

    componentDidMount() {
        store.subscribe(this.onHandleSetState)
        getBanner()
        // getNewsong()
        getTopSong()
    }

    onHandleSetState = () => {
        this.setState(store.getState())
    }

    // onNewsongPlayClick = (index) => {
    //     console.log('parent', index)
    //     const action = {
    //         type: 'save_click_index',
    //         index
    //     }
    //     store.dispatch(action)
    // }

    onNewsongPlayClick = (value) => {
        const ids = value.toString()
        getSongUrlById(ids)
    }

    onBackToTopClick = () => {
        scrollTo(0,0)
    }

    render(){
        return(
            <div className="home-div">
                <div>
                    <Header/>
                </div>
                <div>
                    <Banner
                        bannerList={this.state.bannerList}
                    />
                </div>
                <div className="home-newsong">
                    {this.state.newSongList1.map((value, index)=>{
                        return (
                            <NewSongItem
                                value={value}
                                index={index}
                                newsongId={value.id}
                                onNewsongPlayClick={this.onNewsongPlayClick}
                                mp3Url={this.state.newSongUrl}
                            />
                        )
                    })}
                </div>
                <div className="home-footer">
                    <Footer/>
                </div>
                <div>
                    <BackTop>
                        <i
                            className="iconfont icon-zhiding"
                            onClick={this.onBackToTopClick}
                        ></i>
                    </BackTop>
                </div>
            </div>
        )
    }
}

export default Home;
