import React, {Component} from "react";
import '../../statics/style/audio.css'

class Audio extends Component {
    constructor(props) {
        super(props);
        this.state = {
            url: this.props.audioUrl,
            currentTime: "00:00",
            totalTime: "00:00",
            isPlay: false
        };
        this.outside = React.createRef();
        this.inside = React.createRef();
        this.audio = React.createRef();
    }

    componentDidMount() {
        this.canplay();
        this.timeUpdate();
    }

    formatTime = (value) => {
        const min = Math.floor(value / 60).toString().padStart(2, '0')
        const sec = Math.floor(value % 60).toString().padStart(2, '0')
        return `${min}:${sec}`
    }

    //在音频可以播放时就显示总时长
    canplay = () => {
        this.audio.current.oncanplay = () => {
            const totalTime = this.formatTime(this.audio.current.duration)
            this.setState({totalTime})
        }
    }


    //在音频播放时当前时间也跟着变化
    timeUpdate = () => {
        this.audio.current.ontimeupdate = () => {
            const currentTime = this.formatTime(this.audio.current.currentTime)
            this.setState({currentTime})
            this.inside.current.style.width = (this.audio.current.currentTime / this.audio.current.duration) * 100 + '%'
        }
    }

    audioPlay = () => {
        this.setState({
            isPlay: true
        })
        this.audio.current.play()
    }

    audioStop = () => {
        this.setState({
            isPlay: false
        })
        this.audio.current.pause()
    }

    onProgressChange = (e) => {
        const outsideWidth = this.outside.current.offsetWidth;
        const insideWidth = e.targetTouches[0].clientX
        if (insideWidth > outsideWidth || insideWidth < 0) { return; }
        this.inside.current.style.width = insideWidth + 'px'
        this.audio.current.currentTime =
            (insideWidth / outsideWidth) * this.audio.current.duration;
    }

    render(){
        return (
            <div className="audio">
                <span>{this.state.currentTime}</span>
                <div
                    className="audio-outside"
                    ref={this.outside}
                    onTouchMove={(e) => this.onProgressChange(e)}
                    onTouchStart={(e) => this.onProgressChange(e)}
                >
                    <div
                        className="audio-inside"
                        ref={this.inside}
                    >
                        <div className="audio-inside-btn"></div>
                    </div>
                </div>
                <span>{this.state.totalTime}</span>
                    <audio
                        ref={this.audio}
                        src={this.state.url}
                    ></audio>
                <div className="audio-btn">
                    {!this.state.isPlay
                        ? <i
                            className="iconfont icon-bofang"
                            onClick={this.audioPlay}
                        ></i>
                        : <i
                            className="iconfont icon-zanting"
                            onClick={this.audioStop}
                        ></i>
                    }
                </div>
            </div>
        )
    }
}

export default Audio;
